mapper.select:
select board_num, q.m_no, m.m_id, board_pass, board_subject, board_content, board_re_ref, board_re_lev, board_re_seq, board_readcount, board_date, q.ib_no, ib_img from member m, (select @ROWNUM:=@ROWNUM+1 AS ROWNUM, qna.* from qna, (select @ROWNUM:=0)R order by board_re_ref asc, board_re_seq desc) q left outer join indie_book ib where q.m_no = m.m_no
and q.m_no = #{m_no}
and q.ib_no = #{ib_no}
and board_subject like CONCAT('%',#{query},'%') or board_content like CONCAT('%',#{query},'%')
and board_subject like CONCAT('%',#{query},'%')
and board_content like CONCAT('%',#{query},'%')
GROUP BY ib_no limit #{startRow}, #{limit};

select count(board_num) from qna where 0 = 0

	and m_no = #{m_no}


	and ib_no = #{ib_no}


	
	and board_subject like CONCAT('%',#{query},'%') or board_content like CONCAT('%',#{query},'%')
	
	
	and board_subject like CONCAT('%',#{query},'%')
	
	
	and board_content like CONCAT('%',#{query},'%')
	

;

mapper.update:
update qna 
	set board_re_seq = board_re_seq + 1 
	where board_re_ref = #{re_ref} and board_re_seq > #{re_seq}
